/*
 * This provides a list of node types that have at one time been included with
 * the core of RED-Stack but have since moved out to their own module.
 *
 * If a user has a flow that depends on one of these types and they do not have
 * the new module installed, this will help them identify the missing module.
 */
var nodes = {
    "irc in":     {module:"red-stack-node-irc"},
    "irc out":    {module:"red-stack-node-irc"},
    "irc-server": {module:"red-stack-node-irc"},

    "arduino in":    {module:"red-stack-node-arduino"},
    "arduino out":   {module:"red-stack-node-arduino"},
    "arduino-board": {module:"red-stack-node-arduino"},

    "redis out": {module:"red-stack-node-redis"},

    "mongodb": {module:"red-stack-node-mongodb"},
    "mongodb out": {module:"red-stack-node-mongodb"},

    "serial in": {module:"red-stack-node-serialport"},
    "serial out": {module:"red-stack-node-serialport"},
    "serial-port": {module:"red-stack-node-serialport"},

    "twitter-credentials": {module:"red-stack-node-twitter"},
    "twitter in": {module:"red-stack-node-twitter"},
    "twitter out": {module:"red-stack-node-twitter"},

    "e-mail": {module:"red-stack-node-email"},
    "e-mail in": {module:"red-stack-node-email"},

    "feedparse": {module:"red-stack-node-feedparser"},

    "sentiment": {module:"red-stack-node-sentiment"},

    "tail": {module:"red-stack-node-tail"},

    "rpi-gpio in": {module:"red-stack-node-pi-gpio"},
    "rpi-gpio out": {module:"red-stack-node-pi-gpio"},
    "rpi-mouse": {module:"red-stack-node-pi-gpio"},
    "rpi-keyboard": {module:"red-stack-node-pi-gpio"}
}

module.exports = {
    get: function(id) {
        return nodes[id];
    }
}
